mapper.select:
SELECT customer_id, customer_name, customer_tel
FROM customer
WHERE customer.customer_name = #{name}

SELECT customer_id customerId, customer_name customerName, customer_tel customerTel
FROM customer c
WHERE c.customer_id = #{id}

mapper.update:
UPDATE customer
SET customer.customer_name = #{name}
WHERE customer.customer_id = #{id}

UPDATE customer
SET customer.customer_tel = #{tel}
WHERE customer.customer_id = #{id}
