mapper.update:
UPDATE user
SET user.user_name = #{name}
WHERE user.user_id = #{userId}

UPDATE user
SET user.password = #{password}
WHERE user.user_id = #{userId}

mapper.select:
SELECT user_id userId, user_name userName, password userPassword
FROM user
WHERE user.user_id = #{userId}

select *
from user


        user_id = #{userId}


        # 用户名改成了模糊搜索
        and user_name like #{userName}


        and user_password = #{userPassword}


        and user_character = #{status}
